mapper.delete:
delete from emp
where eno in 

    #{eno}

mapper.update:
update emp
set ename = #{ename},
    sex = #{sex},
    phoneNumber = #{phoneNumber},
    dno = #{dno}
where eno = #{eno};

mapper.select:
select e.eno,ename,sex,phoneNumber,dname,position,pwd from emp e left join dept d on e.dno = d.dno left join user u on e.eno = u.eno
ename like CONCAT('%',#{ename},'%')
and e.dno = #{dno}

select e.eno,ename,sex,phoneNumber,dno,pwd,position from emp e
left join user u on e.eno = u.eno
where e.eno = #{eno}
